var t,h,m,s,str,tt,pt=document.getElementById("pt");function putdate(){t=new Date,h=t.getHours(),m=t.getMinutes(),s=t.getSeconds(),str=h>=12?"PM":"AM",h%=12,pt.innerHTML=checkTime(h)+":"+checkTime(m)+":"+checkTime(s)+" "+str,timer()}function checkTime(t){return t<10&&(t="0"+t),t}function timer(){tt=setTimeout(putdate,1e3)}timer();